mapper.select:
select id, title , content , comment , deleted_at , created_at , updated_at , count(1) over() total_count from qnas where 1 = 1 and deleted_at is null and user_id = #{userPk}
and title like '%'|| #{searchStr} ||'%'
and created_at between to_date(#{startDate}, 'yyyy-mm-dd') and to_date(#{endDate}, 'yyyy-mm-dd') + 1
order by updated_at desc
limit #{length} offset #{beginNo}

mapper.update:
update qnas set
       title = #{title}
     , content = #{content}
     , comment = #{comment}
     , updated_at = now()
 where id = #{id}

update qnas
   set deleted_at = now()
 where id = #{id}
