mapper.select:
select i1.shop_id , max(i1.name) as "name" , max(user_name) as user_name , max(i1.phone) as phone , sum(case when sd.screen_ratio = '1' then 1 else 0 end) x_ratio_count , sum(case when sd.screen_ratio = '2' then 1 else 0 end) y_ratio_count , count(sd.shop_device_id) device_count , sum(case when extract(epoch from (now() - sd.last_check_at::timestamp))::integer < 60 then 1 else 0 end ) active_count , max(i1.created_at) created_at , max(i1.status) as status , total_count from ( select s.shop_id , s."name" , s.status , s.phone , s.created_at , u.name as user_name , u.phone as user_phone , count(1) over() total_count from shop s join cms_user u on u.shop_id = s.shop_id where 1 = 1 order by s.created_at desc
limit #{length} offset #{beginNo}
) i1 join shop_device sd on sd.shop_id = i1.shop_id group by i1.shop_id, total_count
